mapper.select:
SELECT count (*) FROM ( SELECT SF.STAFF_NO FROM STAFF SF LEFT OUTER JOIN STAFF_SKILL SS ON SF.STAFF_NO = SS.STAFF_NO JOIN CODE_SKILL CS ON SS.SKILL_CODE = CS.SKILL_CODE LEFT OUTER JOIN CODE_DEPARTMENT CDM ON SF.DEPARTMENT_CODE = CDM.DEPARTMENT_CODE LEFT OUTER JOIN CODE_SCHOOL CSL ON SF.SCHOOL_CODE = CSL.SCHOOL_CODE
AND SF.staff_name LIKE '%' || #{staffname} || '%'
AND SF.staff_gender = #{staffgender}
AND CDM.department_name = #{departmentname}
AND CSL.school_name = #{schoolname}
AND CS.skill_name IN
#{skillnameOne}
AND SF.graduate_day <= TO_DATE(#{graduatedayOne},'yy-MM-dd')
AND SF.graduate_day >= TO_DATE(#{graduatedayOne},'yy-MM-dd')
GROUP BY sf.STAFF_NO)

SELECT X.RN, X.staff_no, X.staff_name, X.staff_gender, X.department_name, X.graduate_day FROM( SELECT ROWNUM as RN, O.staff_no, O.staff_name, O.staff_gender, O.department_name, O.graduate_day FROM(SELECT A.staff_no, A.staff_name, A.staff_gender, A.department_name, A.graduate_day, A.skill_name FROM (SELECT c_skill.*, c_depart.*, c_school.*, s_taff.staff_no, s_taff.staff_name, s_taff.staff_gender, s_taff.jumin_no, s_taff.graduate_day, s_skill.staff_skill_no FROM code_skill c_skill, code_department c_depart, code_school c_school, staff s_taff, staff_skill s_skill WHERE s_taff.department_code = c_depart.department_code AND s_taff.school_code = c_school.school_code AND s_taff.staff_no = s_skill.staff_no AND s_skill.skill_code = c_skill.skill_code) A
AND A.staff_name LIKE '%' || #{staffname} || '%'
AND A.staff_gender = #{staffgender}
AND A.department_name = #{departmentname}
AND A.school_name = #{schoolname}
AND A.skill_name IN
#{skillnameOne}
AND A.graduate_day <= TO_DATE(#{graduatedayOne},'yy-MM-dd')
AND A.graduate_day >= TO_DATE(#{graduatedayOne},'yy-MM-dd')
ORDER BY A.staff_no DESC) O WHERE ROWNUM <= #{endBoardNo}) X WHERE X.RN >= #{startBoardNo}

SELECT X.RN, X.staff_no, X.staff_name, X.staff_gender, X.department_name, X.graduate_day FROM( SELECT ROWNUM as RN, O.staff_no, O.staff_name, O.staff_gender, O.department_name, O.graduate_day FROM( SELECT SF.staff_no, SF.staff_name, SF.staff_gender, CDM.department_name, SF.graduate_day FROM staff SF LEFT OUTER JOIN STAFF_SKILL SS ON SF.STAFF_NO = SS.STAFF_NO JOIN CODE_SKILL CS ON SS.SKILL_CODE = CS.SKILL_CODE left OUTER JOIN CODE_DEPARTMENT CDM ON SF.DEPARTMENT_CODE = CDM.DEPARTMENT_CODE left OUTER JOIN CODE_SCHOOL CSL ON SF.SCHOOL_CODE = CSL.SCHOOL_CODE
AND SF.staff_name LIKE '%' || #{staffname} || '%'
AND SF.staff_gender = #{staffgender}
AND CDM.department_name = #{departmentname}
AND CSL.school_name = #{schoolname}
AND CS.skill_name IN
#{skillnameOne}
AND SF.graduate_day <= TO_DATE(#{graduatedayOne},'yy-MM-dd')
AND SF.graduate_day >= TO_DATE(#{graduatedayOne},'yy-MM-dd')
GROUP BY SF.STAFF_NO, SF.staff_name, SF.staff_gender, CDM.department_name, SF.graduate_day ORDER BY SF.staff_NO DESC) O WHERE ROWNUM <= #{endBoardNo}) X WHERE X.RN >= #{startBoardNo}
